public class Configure {
	private String indexPath = "";
	private String textRootPath = "";
	
	/**
	 * @return Returns the indexPath.
	 */
	public String getIndexPath() {
		return indexPath;
	}
	/**
	 * @param indexPath The indexPath to set.
	 */
	public void setIndexPath(String indexPath) {
		this.indexPath = indexPath;
	}
	/**
	 * @return Returns the textRootPath.
	 */
	public String getTextRootPath() {
		return textRootPath;
	}
	/**
	 * @param textRootPath The textRootPath to set.
	 */
	public void setTextRootPath(String textRootPath) {
		this.textRootPath = textRootPath;
	}
}
